export async function getServerSideProps(context) {
  const pageQuery = (context.query?.page || 1) as string;
  const take = 2;
  console.log('context--->', pageQuery )

  const first = String(parseInt(pageQuery) * parseInt(take) - parseInt(take) )
  // const skip = '0'
  const { loading, data } = await client.query({
    query: ALL_PRODUCTS,
    variables: {
      skip: first,
      take: '4'
    }

  })
  const products = Object.keys(data).map((k) => data[k])[0]
  return {props: { loading, products }}
}

export interface MaterialUiLinkProps {
    item: PaginationRenderItemParams;
    query: ParsedUrlQuery;
}

export function MaterialUiLink({ item, query, ...props }: MaterialUiLinkProps) {
    return (
      <Link
        href={{
            pathname: '/products',
            query: { ...query, page: item.page },
        }}
      >
          <a {...props}></a>
      </Link>
    );
}
