import React, {useState} from 'react'
import useStyles from './style'
import { PaginationRenderItemParams } from '@material-ui/lab';
import {
  Card,
  CardMedia,
  CardContent,
  Typography,
  CardActionArea,
  CardActions,
  Button,
  Backdrop, Fade
} from '@material-ui/core'
import {GetStaticPaths, GetStaticProps} from "next";
import Loading from "../../components/Loading";
import {gql} from "@apollo/client";
import client from "../apollo";
import {Product} from "../../interfaces";
import Link from "next/link";
import {useRouter} from "next/router";
import Pagination from '@material-ui/lab/Pagination';
import PaginationItem from '@material-ui/lab/PaginationItem';
import Skeleton from '@material-ui/lab/Skeleton';
import Box from '@material-ui/core/Box';
import { ParsedUrlQuery } from 'querystring';
import { PrismaClient } from "@prisma/client"
import {request} from "graphql-request";
import useSWR, {mutate, trigger} from 'swr'
const prisma2 = new PrismaClient()
// import Modal from '../../components/Modal';

import {Modal} from '@material-ui/core';
const take = '3';

const API = 'http://localhost:3000/api/graphql'
const fetcher = (query: any, skip: string, take: string) => request(API, query, {skip, take});


const ALL_PRODUCTS = gql`
    query allProducts($skip: String!, $take: String!) {
        allProducts(skip: $skip, take: $take) {
            id
            name
        }
    }
`

const ALL_PRODUCTS2 = /* GraphQL */`
    query allProducts($skip: String!, $take: String!) {
        allProducts(skip: $skip, take: $take) {
            id
            name
        }
    }
`;

/* GraphQL */
const REMOVE_PRODUCT = /* GraphQL */`
    mutation removeProductById($id: String!) {
        removeProductById(id: $id) {
            id
            name
        }
    }
`

/* GraphQL */
const CREATE_PRODUCT2 = /* GraphQL */`
    mutation createNewOneProduct($name: String!) {
        createNewOneProduct(name: $name) {
            id
            name
        }
    }
`

export interface IProducts {
  products: Product[]
  numberPages: number,
  skip: string
}

export default function Products({ skip, products, numberPages}: IProducts) {
  const classes = useStyles();
  const {data}  = useSWR([ALL_PRODUCTS2, skip, take], { initialData: products})
  const updatedProducts : Product[] = data! || [];
  const loading: boolean = !data;
  // console.log('products1', products)
  console.log('data......---', data)



  const router = useRouter();
  if( router.isFallback || loading) {
    return <Loading/>
  }

  //modal function
  const [open, setOpen] = useState(false);

  const handleOpen = (e: any) => {
    console.log('event delete', e)
    e.preventDefault()
    e.stopPropagation();
    e.nativeEvent.stopImmediatePropagation();
    setOpen(true);
  };

  const handleClose =  (e: any) => {
    e.preventDefault();
    e.stopPropagation();
    setOpen(false);
  };

  const handleRemoveInModal = async (e, id: string) => {
    console.log('event remove', e)
    e.preventDefault()
    e.stopPropagation();
    e.nativeEvent.stopImmediatePropagation();
    // e.preventDefault();
    console.log('id...', id)
    await mutate(API);

    // const data1 = await request(API, REMOVE_PRODUCT, {id: id})
    const {createNewOneProduct} = await request(API, CREATE_PRODUCT2, {name: id})
    console.log('data check.....-> ', createNewOneProduct)
    await trigger(API);
  }

  const productArray = Object.keys(updatedProducts).map((k) => updatedProducts[k])[0]
  console.log('productArray[0]',productArray[0])
const [activeProduct, setActiveProduct] = useState(null);

  return (
    <div className={classes.wrapper}>
      <div className={classes.content__tittle}>
        <div>POPULAR PRODUCTS</div>
      </div>
      <Box className={classes.content__pagination}>
        {
          (numberPages > 0 || !router.isFallback)? (<Pagination
              page={parseInt(router.query.page as string || '1')}
              count={numberPages}
              defaultPage={1}
              renderItem={(item) => (
                <PaginationItem
                  component={MaterialUiLink}
                  query={router.query}
                  item={item}
                  {...item}
                />
              )}
            />) :
            <Skeleton variant="rect" width={'30vw'} height={'28px'} />
        }
      </Box>
      <div className={classes.content__items}>
        {
          ((products && products.length > 0) || !router.isFallback) ?
            (productArray?.map( (p, i)=> {
              return (<Link href="/products/details/[id]" as={`/products/details/${p.id}`}>
                <Card key = {i} className={classes.content__item}>
                  <CardActionArea>
                    <CardMedia className={classes.item__media}
                               image="https://cdn.bike24.net/i/mb/d8/fa/b2/277893-00-d-557791.jpg"
                               title="Contemplative Reptile"
                    />
                    <CardContent>
                      <Typography gutterBottom variant="h5" component="h2">
                        {p?.name}
                      </Typography>
                      <Typography variant="body2" color="textSecondary" component="p">
                        Maximum impact cushioning. The brutal, repetitive,
                        downward force of sport can wreak havoc on the body and on performance.
                        Max Air cushioning is specifically engineered to handle these impacts and provide protection.
                        Max Air is big air designed to take a pounding.
                      </Typography>
                      <Typography className={classes.item__price} >PRICE: 60.0 Euro</Typography>
                    </CardContent>
                  </CardActionArea>
                  <CardActions className={classes.item__buttons}>
                    <Box component="div">
                      <Button size="small" color="primary"  className={classes.button__addToCart}>
                        Add To Cart
                      </Button>
                      <Button size="small" color="primary">
                        Learn More
                      </Button>
                    </Box>
                    <Button size="small" color="primary" onClick={handleOpen}>
                      Delete
                    </Button>
                  </CardActions>
                  {open? (<Modal
                      className={classes.modal}
                      open={open}
                      onClose={handleClose}
                      closeAfterTransition
                      BackdropComponent={Backdrop}
                      BackdropProps={{
                        timeout: 500,
                      }}
                    >
                      <Fade in={open}>
                        <div className={classes.paper}>
                          <h2 id="transition-modal-title">DELETE THIS PRODUCT</h2>
                          <p id="transition-modal-description">Do you want to delete this item?.</p>
                          <Button size="small" variant="contained" color="primary"
                                  onClick={async (e) => {
                                    setActiveProduct(p)
                            handleRemoveInModal(e, p.id)
                          }}>REMOVE</Button>
                          <Button size="small" variant="outlined" color="default" onClick={handleClose}>CANCEL</Button>
                        </div>
                      </Fade>
                    </Modal>)
                    :null
                  }
                </Card>
              </Link>)
            })) :
            <Box className={classes.content__skeleton}>
              <Skeleton variant="rect" width={'28vw'} height={'720px'} />
              <div className={classes.wrapper__loading}>
                <Loading />
              </div>
            </Box>
        }
      </div>

    </div>
  )
}

export const getStaticProps:GetStaticProps = async (ctx) => {
  const totalProducts = await prisma2.product.findMany();
  const productsArray = Object.keys(totalProducts).map((k) => totalProducts[parseInt(k)])
  const numberPages = Math.ceil((productsArray?.length || 0) as number / +take) as number;
  const pageQuery = (ctx?.params?.page || 1) as string;

  const skip = String((parseInt(pageQuery) -1) * (+take))

  // const {  data } = await client.query({
  //   query: ALL_PRODUCTS,
  //   variables: {
  //     skip: skip,
  //     take: take
  //   }
  // })

  const products = await fetcher(ALL_PRODUCTS2, skip, take);
console.log('products2', products)
  // const products = Object.keys(data).map((k) => data[k])[0]
// console.log(' server build...', data)
//   const deleteItem = await fetcher(REMOVE_PRODUCT_BY_ID, skip, take);
// console.log('deleteItem:', deleteItem);
  return {
    props: {
      products,
      numberPages,
      skip
    },
  }
}

export const getStaticPaths:GetStaticPaths<{page:string}> = async () => {
  return {
    paths: [
      { params: { page: '1' } },
      { params: { page: '2' } },
      { params: { page: '3' } }
    ],
    fallback: true
  };
}

export interface MaterialUiLinkProps {
  item: PaginationRenderItemParams;
  query: ParsedUrlQuery;
}

export function MaterialUiLink({ item, query, ...props }: MaterialUiLinkProps) {
  return (
    <Link
      href="/products/[page]"
      as={`/products/${item.page}`}
    >
      <a {...props}></a>
    </Link>
  );
}
