import React, {useState} from 'react';
import {Modal, Backdrop, Fade, Button} from '@material-ui/core';

import {request} from "graphql-request";
import useSWR, {mutate, trigger} from 'swr'
import useStyles from './style';
import Router, {useRouter} from "next/router";

const sleep = (time) => new Promise((acc) => setTimeout(acc, time));

/* GraphQL */
const REMOVE_PRODUCT = /* GraphQL */`
    mutation removeProductById($id: String!) {
        removeProductById(id: $id) {
            id
            name
        }
    }
`

/* GraphQL */
const CREATE_PRODUCT2 = /* GraphQL */ `
  mutation createNewOneProduct($name: String!) {
    createNewOneProduct(name: $name) {
      id
      name
    }
  }
`;

const API = 'http://localhost:3000/api/graphql'


// @ts-ignore
export default function TransitionsModal({currentPage, handleOpen, product, handleClose, open}) {
    const classes = useStyles();

    const handleRemoveInModal = async (e) => {

      console.log("event remove", e);
      e.preventDefault();
      e.stopPropagation();

      await sleep(2000);

      console.log("id...", typeof product, product);
      await mutate(API);
      const {id} = product;
       await request(API, REMOVE_PRODUCT, {id})
      // const { createNewOneProduct } = await request(API, CREATE_PRODUCT2, {
      //   name: idProduct,
      // });

      await trigger(API);

      // await Router.push(`/products/${currentPage}`)
    }

    return (
        <div>
            <Modal
                className={classes.modal}
                open={open}
                onClose={handleClose}
                closeAfterTransition
                BackdropComponent={Backdrop}
                BackdropProps={{
                    timeout: 500,
                }}
            >
                <Fade in={open}>
                    <div className={classes.paper}>
                        <h2 id="transition-modal-title">DELETE THIS PRODUCT</h2>
                        <p id="transition-modal-description">Do you want to delete this item?.</p>
                        <Button size="small" variant="contained" color="primary" onClick={handleRemoveInModal}>REMOVE</Button>
                        <Button size="small" variant="outlined" color="default" onClick={handleClose}>CANCEL</Button>
                    </div>
                </Fade>
            </Modal>
        </div>
    );
}
