import React from 'react';
import {Modal, Backdrop, Fade, Button} from '@material-ui/core';

import {request} from "graphql-request";
import useSWR, {mutate, trigger} from 'swr'
import useStyles from './style';

/* GraphQL */
const REMOVE_PRODUCT = /* GraphQL */`
    mutation removeProductById($id: String!) {
        removeProductById(id: $id) {
            id
            name
        }
    }
`

const API = 'http://localhost:3000/api/graphql'
const fetcher = (query: any, skip: string, take: string) => request(API, query, {skip, take});


// @ts-ignore
export default function TransitionsModal({handleOpen, idProduct, handleClose, open}) {
    const classes = useStyles();

    const handleRemoveInModal = async (e) => {
      e.stopPropagation()
      e.preventDefault();
      await mutate(API);
      const data1 = await request(API, REMOVE_PRODUCT, {id: idProduct})
      console.log('data check.....-> ', data1)
      await trigger(API);
    }

    return (
        <div>
            <Modal
                className={classes.modal}
                open={open}
                onClose={handleClose}
                closeAfterTransition
                BackdropComponent={Backdrop}
                BackdropProps={{
                    timeout: 500,
                }}
            >
                <Fade in={open}>
                    <div className={classes.paper}>
                        <h2 id="transition-modal-title">DELETE THIS PRODUCT</h2>
                        <p id="transition-modal-description">Do you want to delete this item?.</p>
                        <Button size="small" variant="contained" color="primary" onClick={handleRemoveInModal}>REMOVE</Button>
                        <Button size="small" variant="outlined" color="default" onClick={handleClose}>CANCEL</Button>
                    </div>
                </Fade>
            </Modal>
        </div>
    );
}
